mapper.select:
select count(*) from student_score

	AND `学号` like '%${id}%'
	AND `姓名` like '%${name}%'
	AND `成绩等级` = #{level}
	and ${scoreN} between ${scoreU} and ${scoreD}

select * from student_score

	AND `学号` like '%${id}%'
	AND `姓名` like '%${name}%'
	AND `成绩等级` = #{level}
	and ${scoreN} between ${scoreU} and ${scoreD}
	order by ${sortType} ${sort}

LIMIT #{index},#{pageMax}
